<span class="line"><span class="token keyword">import</span> smtplib</span>
<span class="line"><span class="token keyword">from</span> email<span class="token punctuation">.</span>mime<span class="token punctuation">.</span>image <span class="token keyword">import</span> MIMEImage</span>
<span class="line"></span>
<span class="line"><span class="token keyword">def</span> <span class="token function">send_salary_slip</span><span class="token punctuation">(</span>sender_email<span class="token punctuation">,</span> sender_password<span class="token punctuation">,</span> receiver_email<span class="token punctuation">,</span> subject<span class="token punctuation">,</span> message<span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">    <span class="token comment"># 创建邮件内容对象</span></span>
<span class="line">    msg <span class="token operator">=</span> MIMEMultipart<span class="token punctuation">(</span><span class="token punctuation">)</span></span>
<span class="line">    msg<span class="token punctuation">[</span><span class="token string">'From'</span><span class="token punctuation">]</span> <span class="token operator">=</span> sender_email</span>
<span class="line">    msg<span class="token punctuation">[</span><span class="token string">'To'</span><span class="token punctuation">]</span> <span class="token operator">=</span> receiver_email</span>
<span class="line">    msg<span class="token punctuation">[</span><span class="token string">'Subject'</span><span class="token punctuation">]</span> <span class="token operator">=</span> subject</span>
<span class="line">    <span class="token comment">#  将HTML转为图片</span></span>
<span class="line">    config <span class="token operator">=</span> imgkit<span class="token punctuation">.</span>config<span class="token punctuation">(</span>wkhtmltoimage<span class="token operator">=</span> <span class="token string">r"D:\program files\wkhtmltopdf\bin\wkhtmltoimage.exe"</span><span class="token punctuation">)</span></span>
<span class="line">    imgkit<span class="token punctuation">.</span>from_string<span class="token punctuation">(</span>message<span class="token punctuation">,</span> <span class="token string">'./output.png'</span><span class="token punctuation">,</span>config<span class="token operator">=</span>config<span class="token punctuation">)</span></span>
<span class="line">    <span class="token comment"># 添加图片附件</span></span>
<span class="line">    <span class="token keyword">with</span> <span class="token builtin">open</span><span class="token punctuation">(</span><span class="token string">'output.png'</span><span class="token punctuation">,</span> <span class="token string">'rb'</span><span class="token punctuation">)</span> <span class="token keyword">as</span> f<span class="token punctuation">:</span></span>
<span class="line">        image_part <span class="token operator">=</span> MIMEImage<span class="token punctuation">(</span>f<span class="token punctuation">.</span>read<span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span></span>
<span class="line">        image_part<span class="token punctuation">.</span>add_header<span class="token punctuation">(</span><span class="token string">'Content-Disposition'</span><span class="token punctuation">,</span> <span class="token string">'attachment'</span><span class="token punctuation">,</span> filename<span class="token operator">=</span><span class="token string">'output.png'</span><span class="token punctuation">)</span></span>
<span class="line">        msg<span class="token punctuation">.</span>attach<span class="token punctuation">(</span>image_part<span class="token punctuation">)</span></span>
<span class="line">    <span class="token comment"># 添加邮件正文</span></span>
<span class="line">    msg<span class="token punctuation">.</span>attach<span class="token punctuation">(</span>MIMEText<span class="token punctuation">(</span>message<span class="token punctuation">,</span> <span class="token string">'html'</span><span class="token punctuation">)</span><span class="token punctuation">)</span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">    <span class="token comment"># 连接到SMTP服务器</span></span>
<span class="line">    server <span class="token operator">=</span> smtplib<span class="token punctuation">.</span>SMTP<span class="token punctuation">(</span><span class="token string">'smtp.qq.com'</span><span class="token punctuation">)</span>  <span class="token comment"># 这里使用的是Gmail的SMTP服务器，如果使用其他邮箱，请查阅相应的SMTP服务器地址和端口号</span></span>
<span class="line">    server<span class="token punctuation">.</span>starttls<span class="token punctuation">(</span><span class="token punctuation">)</span></span>
<span class="line"></span>
<span class="line">    <span class="token keyword">try</span><span class="token punctuation">:</span></span>
<span class="line">        <span class="token comment"># 登录邮箱</span></span>
<span class="line">        server<span class="token punctuation">.</span>login<span class="token punctuation">(</span>sender_email<span class="token punctuation">,</span> sender_password<span class="token punctuation">)</span></span>
<span class="line"></span>
<span class="line">        <span class="token comment"># 发送邮件</span></span>
<span class="line">        server<span class="token punctuation">.</span>sendmail<span class="token punctuation">(</span>sender_email<span class="token punctuation">,</span> receiver_email<span class="token punctuation">,</span> msg<span class="token punctuation">.</span>as_string<span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">)</span></span>
<span class="line">        <span class="token keyword">print</span><span class="token punctuation">(</span><span class="token string">"邮件发送成功"</span><span class="token punctuation">)</span></span>
<span class="line">    <span class="token keyword">except</span> Exception <span class="token keyword">as</span> e<span class="token punctuation">:</span></span>
<span class="line">        <span class="token keyword">print</span><span class="token punctuation">(</span><span class="token string">"邮件发送失败:"</span><span class="token punctuation">,</span> <span class="token builtin">str</span><span class="token punctuation">(</span>e<span class="token punctuation">)</span><span class="token punctuation">)</span></span>
<span class="line">    <span class="token keyword">finally</span><span class="token punctuation">:</span></span>
<span class="line">        <span class="token comment"># 关闭连接</span></span>
<span class="line">        server<span class="token punctuation">.</span>quit<span class="token punctuation">(</span><span class="token punctuation">)</span></span>
