<span class="line">year_list<span class="token punctuation">.</span>append<span class="token punctuation">(</span><span class="token builtin">str</span><span class="token punctuation">(</span>year<span class="token punctuation">)</span><span class="token punctuation">)</span></span>
<span class="line"></span>
<span class="line"><span class="token keyword">while</span> i<span class="token operator">&lt;</span><span class="token number">4</span><span class="token punctuation">:</span></span>
<span class="line"></span>
<span class="line">    month<span class="token operator">=</span>month<span class="token operator">-</span><span class="token number">3</span></span>
<span class="line"></span>
<span class="line">    <span class="token keyword">if</span> month <span class="token operator">==</span><span class="token number">0</span><span class="token punctuation">:</span></span>
<span class="line">        month <span class="token operator">=</span> <span class="token number">12</span></span>
<span class="line">        year <span class="token operator">=</span> year<span class="token operator">-</span><span class="token number">1</span></span>
<span class="line"></span>
<span class="line">    year_list<span class="token punctuation">.</span>append<span class="token punctuation">(</span><span class="token builtin">str</span><span class="token punctuation">(</span>year<span class="token punctuation">)</span><span class="token punctuation">)</span></span>
<span class="line">    month_list<span class="token punctuation">.</span>append<span class="token punctuation">(</span>month<span class="token punctuation">)</span></span>
