<span class="line"></span>
<span class="line"><span class="token comment"># print(month_day[str(month_list[0])])</span></span>
<span class="line"></span>
<span class="line">stock_plate <span class="token operator">=</span> <span class="token punctuation">[</span><span class="token string">"sz"</span><span class="token punctuation">,</span><span class="token string">"SZ"</span><span class="token punctuation">,</span><span class="token string">"sh"</span><span class="token punctuation">,</span><span class="token string">"SH"</span><span class="token punctuation">]</span></span>
<span class="line"><span class="token triple-quoted-string string">"""判断其板块"""</span></span>
<span class="line"></span>
<span class="line"><span class="token keyword">if</span> stock_code<span class="token punctuation">.</span>startswith<span class="token punctuation">(</span><span class="token string">"6"</span><span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">    stock_demo <span class="token operator">=</span> <span class="token string">"sh"</span><span class="token operator">+</span>stock_code</span>
<span class="line">    stock_demo1 <span class="token operator">=</span> <span class="token string">"SH"</span><span class="token operator">+</span>stock_code</span>
<span class="line"><span class="token keyword">elif</span> stock_code<span class="token punctuation">.</span>startswith<span class="token punctuation">(</span><span class="token punctuation">(</span><span class="token string">"0"</span><span class="token punctuation">,</span><span class="token string">"3"</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">    stock_demo <span class="token operator">=</span> <span class="token string">"sz"</span> <span class="token operator">+</span> stock_code</span>
<span class="line">    stock_demo1 <span class="token operator">=</span> <span class="token string">"SZ"</span> <span class="token operator">+</span> stock_code</span>
<span class="line"><span class="token keyword">else</span><span class="token punctuation">:</span></span>
<span class="line">    <span class="token keyword">print</span><span class="token punctuation">(</span><span class="token string">"无法判断股票代码:"</span><span class="token punctuation">)</span></span>
<span class="line"></span>
<span class="line"></span>
<span class="line">url <span class="token operator">=</span> <span class="token string-interpolation"><span class="token string">f"http://emweb.eastmoney.com/PC_HSF10/NewFinanceAnalysis/Index?type=web&amp;code=</span><span class="token interpolation"><span class="token punctuation">{</span>stock_demo<span class="token punctuation">}</span></span><span class="token string">"</span></span></span>
<span class="line">resp <span class="token operator">=</span> requests<span class="token punctuation">.</span>get<span class="token punctuation">(</span>url<span class="token punctuation">,</span> timeout<span class="token operator">=</span><span class="token number">40</span><span class="token punctuation">)</span></span>
