<span class="line">zcfzb_model <span class="token operator">=</span> re<span class="token punctuation">.</span><span class="token builtin">compile</span><span class="token punctuation">(</span><span class="token string">r'&lt;script type="text/template" id="zcfzb_qy">.*?&lt;/script>'</span></span>
<span class="line">                         <span class="token punctuation">,</span>re<span class="token punctuation">.</span>S<span class="token punctuation">)</span></span>
<span class="line"><span class="token comment"># 提取tbody标签内容</span></span>
<span class="line">zcfzb_mode0 <span class="token operator">=</span> re<span class="token punctuation">.</span><span class="token builtin">compile</span><span class="token punctuation">(</span><span class="token string">r'(&lt;tbody>\s+&lt;tr>\s+&lt;th class="tips-colname-Left" style="width: 366px;">.*?&lt;/tbody>)'</span></span>
<span class="line">                         <span class="token punctuation">,</span>re<span class="token punctuation">.</span>S<span class="token punctuation">)</span></span>
<span class="line"></span>
<span class="line"><span class="token comment">#提取name和数据代码</span></span>
<span class="line">name_model <span class="token operator">=</span> re<span class="token punctuation">.</span><span class="token builtin">compile</span><span class="token punctuation">(</span><span class="token string">r'\s+([\u4e00-\u9fa5].*?)\s+'</span><span class="token punctuation">,</span>re<span class="token punctuation">.</span>S<span class="token punctuation">)</span></span>
<span class="line">name1_model <span class="token operator">=</span> re<span class="token punctuation">.</span><span class="token builtin">compile</span><span class="token punctuation">(</span><span class="token string">r'\s+(其中:\w*?)\s+'</span><span class="token punctuation">,</span>re<span class="token punctuation">.</span>S<span class="token punctuation">)</span></span>
<span class="line">date_model <span class="token operator">=</span> re<span class="token punctuation">.</span><span class="token builtin">compile</span><span class="token punctuation">(</span><span class="token string">r"\{\{format\w*?\(value.(?P&lt;date>\w*?)\)}}"</span><span class="token punctuation">,</span>re<span class="token punctuation">.</span>S<span class="token punctuation">)</span></span>
<span class="line"><span class="token comment">#使用re提取</span></span>
<span class="line">zcf <span class="token operator">=</span>zcfzb_model<span class="token punctuation">.</span>findall<span class="token punctuation">(</span>resp<span class="token punctuation">.</span>text<span class="token punctuation">)</span></span>
