<span class="line">    dic <span class="token operator">=</span> <span class="token punctuation">{</span><span class="token punctuation">}</span></span>
<span class="line">    <span class="token comment"># print(tr.text)</span></span>
<span class="line">    names<span class="token operator">=</span>name_model<span class="token punctuation">.</span>findall<span class="token punctuation">(</span>tr<span class="token punctuation">.</span>text<span class="token punctuation">)</span></span>
<span class="line">    names1<span class="token operator">=</span>name1_model<span class="token punctuation">.</span>findall<span class="token punctuation">(</span>tr<span class="token punctuation">.</span>text<span class="token punctuation">)</span></span>
<span class="line">    date <span class="token operator">=</span>date_model<span class="token punctuation">.</span>findall<span class="token punctuation">(</span>tr<span class="token punctuation">.</span>text<span class="token punctuation">)</span></span>
<span class="line">    <span class="token comment"># print(names,names1)</span></span>
<span class="line">    <span class="token keyword">if</span> names1<span class="token punctuation">:</span></span>
<span class="line">        <span class="token comment"># print(names1)</span></span>
<span class="line">        name_str <span class="token operator">=</span><span class="token string">""</span><span class="token punctuation">.</span>join<span class="token punctuation">(</span>names1<span class="token punctuation">)</span></span>
<span class="line">        dic<span class="token punctuation">[</span><span class="token string">"name"</span><span class="token punctuation">]</span><span class="token operator">=</span>name_str</span>
