<span class="line">    col <span class="token operator">=</span> <span class="token builtin">str</span><span class="token punctuation">(</span>date_list<span class="token punctuation">.</span>index<span class="token punctuation">(</span>i<span class="token punctuation">)</span><span class="token operator">+</span><span class="token number">1</span><span class="token punctuation">)</span></span>
<span class="line"></span>
<span class="line">    <span class="token keyword">for</span> n <span class="token keyword">in</span> lis<span class="token punctuation">:</span></span>
<span class="line">        <span class="token comment"># 获得当前得到的是哪个字典的数据</span></span>
<span class="line">        <span class="token comment"># print(n)</span></span>
