<span class="line">        <span class="token keyword">except</span><span class="token punctuation">:</span></span>
<span class="line">            <span class="token keyword">if</span> n<span class="token punctuation">[</span><span class="token string">"name"</span><span class="token punctuation">]</span><span class="token operator">==</span><span class="token string">"吸收存款及同业存放"</span><span class="token punctuation">:</span></span>
<span class="line">              info <span class="token operator">=</span> <span class="token string">""</span></span>
<span class="line">              lis<span class="token punctuation">[</span>index<span class="token punctuation">]</span><span class="token punctuation">[</span>col<span class="token punctuation">]</span> <span class="token operator">=</span> info</span>
<span class="line">            <span class="token keyword">else</span><span class="token punctuation">:</span></span>
<span class="line">                info <span class="token operator">=</span> <span class="token string">"--"</span></span>
<span class="line">                <span class="token comment"># print(info)</span></span>
<span class="line">                <span class="token comment"># print(type(info))</span></span>
<span class="line">                lis<span class="token punctuation">[</span>index<span class="token punctuation">]</span><span class="token punctuation">[</span>col<span class="token punctuation">]</span> <span class="token operator">=</span> info</span>
<span class="line">                <span class="token comment"># print("一个项目的数据完成了---\n")</span></span>
