<span class="line"></span>
<span class="line"><span class="token keyword">for</span> a <span class="token keyword">in</span> lis<span class="token punctuation">:</span></span>
<span class="line">    row_n <span class="token operator">=</span> lis<span class="token punctuation">.</span>index<span class="token punctuation">(</span>a<span class="token punctuation">)</span></span>
<span class="line">    <span class="token comment"># print(row_n)</span></span>
<span class="line">    col<span class="token operator">=</span> <span class="token number">0</span></span>
<span class="line">    <span class="token keyword">for</span> v <span class="token keyword">in</span> a<span class="token punctuation">.</span>values<span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">        <span class="token keyword">if</span> col<span class="token operator">&lt;</span>cols<span class="token punctuation">:</span></span>
<span class="line">            ws<span class="token punctuation">.</span>cell<span class="token punctuation">(</span>row_n<span class="token operator">+</span><span class="token number">1</span><span class="token punctuation">,</span>col<span class="token operator">+</span><span class="token number">1</span><span class="token punctuation">,</span>v<span class="token punctuation">)</span></span>
<span class="line">            col <span class="token operator">+=</span> <span class="token number">1</span></span>
<span class="line">        <span class="token keyword">else</span><span class="token punctuation">:</span></span>
<span class="line">            <span class="token keyword">print</span><span class="token punctuation">(</span><span class="token string">"出错"</span><span class="token punctuation">)</span></span>
<span class="line"><span class="token comment"># 删除不要的数据，以便美化图表</span></span>
<span class="line">ws<span class="token punctuation">.</span>delete_cols<span class="token punctuation">(</span><span class="token number">2</span><span class="token punctuation">)</span>   <span class="token comment">#删除数据data列</span></span>
<span class="line">max_row<span class="token operator">=</span>ws<span class="token punctuation">.</span>max_row</span>
<span class="line">max_col <span class="token operator">=</span> ws<span class="token punctuation">.</span>max_column</span>
<span class="line">col_list <span class="token operator">=</span> <span class="token punctuation">[</span>j <span class="token keyword">for</span> j <span class="token keyword">in</span> <span class="token builtin">range</span><span class="token punctuation">(</span>max_col<span class="token punctuation">)</span><span class="token punctuation">]</span></span>
<span class="line"><span class="token comment"># print(max_row,man_col)</span></span>
<span class="line">shanchu <span class="token operator">=</span><span class="token punctuation">[</span> <span class="token punctuation">]</span></span>
<span class="line"><span class="token keyword">for</span> r <span class="token keyword">in</span> <span class="token builtin">range</span><span class="token punctuation">(</span>max_row<span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">    <span class="token comment"># 数量限制判断</span></span>
<span class="line">    x <span class="token operator">=</span> <span class="token number">0</span></span>
<span class="line">    <span class="token keyword">for</span> c <span class="token keyword">in</span> <span class="token builtin">range</span><span class="token punctuation">(</span>max_col<span class="token punctuation">)</span><span class="token punctuation">:</span></span>
<span class="line">        <span class="token keyword">if</span> ws<span class="token punctuation">.</span>cell<span class="token punctuation">(</span>row<span class="token operator">=</span>r<span class="token operator">+</span><span class="token number">1</span><span class="token punctuation">,</span> column<span class="token operator">=</span>c<span class="token operator">+</span><span class="token number">1</span><span class="token punctuation">)</span><span class="token punctuation">.</span>value <span class="token keyword">in</span> <span class="token punctuation">[</span><span class="token boolean">None</span><span class="token punctuation">,</span><span class="token string">""</span><span class="token punctuation">,</span><span class="token number">0</span><span class="token punctuation">,</span><span class="token string">"0"</span><span class="token punctuation">]</span><span class="token punctuation">:</span></span>
<span class="line">            x <span class="token operator">+=</span> <span class="token number">1</span></span>
<span class="line">            <span class="token comment"># print("x",x)</span></span>
<span class="line">            <span class="token keyword">if</span> x <span class="token operator">></span><span class="token number">4</span><span class="token punctuation">:</span></span>
<span class="line">                <span class="token comment">#记录要删除的行</span></span>
<span class="line">                shanchu<span class="token punctuation">.</span>append<span class="token punctuation">(</span>r<span class="token operator">+</span><span class="token number">1</span><span class="token punctuation">)</span></span>
<span class="line">            <span class="token keyword">else</span><span class="token punctuation">:</span></span>
<span class="line">                <span class="token keyword">continue</span></span>
<span class="line">        <span class="token keyword">else</span><span class="token punctuation">:</span></span>
<span class="line">            <span class="token keyword">continue</span></span>
<span class="line"><span class="token comment"># print(shanchu)</span></span>
<span class="line"><span class="token comment"># print(len(shanchu))</span></span>
<span class="line">ws1 <span class="token operator">=</span> wb<span class="token punctuation">.</span>create_sheet<span class="token punctuation">(</span><span class="token string">"资产负债表"</span><span class="token punctuation">)</span></span>
<span class="line">dat <span class="token operator">=</span> <span class="token punctuation">[</span>i <span class="token keyword">for</span> i <span class="token keyword">in</span> <span class="token builtin">range</span><span class="token punctuation">(</span><span class="token number">1</span><span class="token punctuation">,</span><span class="token builtin">len</span><span class="token punctuation">(</span>lis<span class="token punctuation">)</span><span class="token operator">+</span><span class="token number">1</span><span class="token punctuation">)</span><span class="token punctuation">]</span></span>
