<span class="line">    <span class="token keyword">for</span> c <span class="token keyword">in</span> col_list<span class="token punctuation">:</span></span>
<span class="line">        r <span class="token operator">=</span> col_list<span class="token punctuation">.</span>index<span class="token punctuation">(</span>c<span class="token punctuation">)</span> <span class="token operator">+</span> <span class="token number">1</span></span>
<span class="line">        ws1<span class="token punctuation">.</span>cell<span class="token punctuation">(</span>q<span class="token punctuation">,</span>r<span class="token punctuation">,</span>ws<span class="token punctuation">.</span>cell<span class="token punctuation">(</span>z<span class="token punctuation">,</span>r<span class="token punctuation">)</span><span class="token punctuation">.</span>value<span class="token punctuation">)</span></span>
